var express=require("express");var path=require("path");var app=express();var os=require("os");app.set("view engine","ejs");console.log(os.platform());app.use(express.static(path.join(__dirname,"/public")));var data={count:0};app.get("/",function(e,t){data.count++;t.render("my_first_ejs",data)});app.get("/reset",function(e,t){data.count=0;t.render("my_first_ejs",data)});app.get("/set/count",function(e,t){if(e.query.count)data.count=e.query.count;t.render("my_first_ejs",data)});app.get("/set/:num",function(e,t){data.count=e.params.num;t.render("my_first_ejs",data)});app.listen(8e3,function(){console.log("Server on!")});